import React, { useState } from "react";
import { updateApiKey } from "./api";

function Admin({ goBack }) {
  const [form, setForm] = useState({
    username: "",
    password: "",
    new_key: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async () => {
    const res = await updateApiKey(form);
    alert(res.data.message || res.data.error);
  };

  return (
    <div>
      <h2 className="text-xl font-bold text-center text-red-600 mb-4">
        Admin Panel
      </h2>

      <input name="username" placeholder="Username" className="input" onChange={handleChange} />
      <input name="password" type="password" placeholder="Password" className="input" onChange={handleChange} />
      <input name="new_key" placeholder="New Gemini API Key" className="input" onChange={handleChange} />

      <button onClick={submit}
        className="w-full bg-red-500 text-white p-3 rounded-lg mt-4">
        Update API Key
      </button>

      <p onClick={goBack}
        className="text-center text-gray-500 mt-4 cursor-pointer">
        Back
      </p>
    </div>
  );
}

export default Admin;
