import React, { useState } from "react";
import { generateResponse } from "./api";
import ReactMarkdown from "react-markdown";

// --- Components for the UI ---

const Navbar = () => (
  <nav className="border-b border-gray-800 bg-black py-4 px-6 flex justify-between items-center sticky top-0 z-50">
    <div className="text-2xl font-black tracking-tighter text-white">
      GIFT<span className="text-purple-500">GENIX</span>
    </div>
    <div className="hidden md:flex space-x-8 text-sm font-medium text-gray-400">
      <a href="#generator" className="hover:text-white transition">Generator</a>
      <a href="#features" className="hover:text-white transition">Features</a>
      <a href="#about" className="hover:text-white transition">About</a>
    </div>
    <button className="bg-white text-black px-5 py-2 rounded-full text-sm font-bold hover:bg-purple-500 hover:text-white transition">
      Pro Plan
    </button>
  </nav>
);

const Footer = () => (
  <footer className="bg-black border-t border-gray-800 py-12 px-6 mt-20">
    <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
      <div>
        <h3 className="text-white font-bold mb-4">GiftGenix AI</h3>
        <p className="text-gray-500 text-sm">Elevating the art of giving through advanced generative intelligence.</p>
      </div>
      <div>
        <h4 className="text-white font-semibold mb-4">Quick Links</h4>
        <ul className="text-gray-500 text-sm space-y-2">
          <li>Privacy Policy</li>
          <li>Terms of Service</li>
          <li>API Documentation</li>
        </ul>
      </div>
      <div>
        <h4 className="text-white font-semibold mb-4">Connect</h4>
        <p className="text-gray-500 text-sm">support@giftgenix.ai</p>
      </div>
    </div>
    <div className="text-center text-gray-600 text-xs mt-12">
      © 2026 GiftGenix Inc. All rights reserved.
    </div>
  </footer>
);

function App() {
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  // --- Logic (Unchanged) ---
  const handleSubmit = async () => {
    if (!prompt) return alert("Enter a prompt");
    setLoading(true);
    setResult("");
    try {
      const res = await generateResponse({ prompt });
      setResult(res.data.result || res.data.error);
    } catch (err) {
      setResult("Server error.");
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-[#0a0a0a] text-gray-200 selection:bg-purple-500/30">
      <Navbar />

      <main>
        {/* Hero Section */}
        <section className="pt-20 pb-12 px-6 text-center">
          <h1 className="text-5xl md:text-7xl font-extrabold text-white mb-6 tracking-tight">
            The Future of <span className="text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-fuchsia-600">Gifting.</span>
          </h1>
          <p className="text-gray-400 max-w-2xl mx-auto text-lg mb-10">
            Stop guessing. Our AI analyzes your sentiment to curate the most 
            meaningful gifts for any occasion.
          </p>
        </section>

        {/* Generator Section */}
        <section id="generator" className="max-w-4xl mx-auto px-6 py-12">
          <div className="bg-[#111] border border-gray-800 rounded-3xl p-1 shadow-2xl">
            <div className="bg-[#161616] rounded-[22px] p-8">
              <label className="block text-xs font-uppercase tracking-widest text-purple-500 font-bold mb-4 uppercase">
                Input Parameters
              </label>
              <textarea
                rows="4"
                placeholder="Describe the person, their hobbies, and the occasion..."
                className="w-full bg-black border border-gray-800 text-white p-4 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-600 transition resize-none placeholder:text-gray-600"
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
              />

              <button
                onClick={handleSubmit}
                disabled={loading}
                className="w-full bg-white text-black font-bold py-4 rounded-xl mt-6 hover:bg-purple-500 hover:text-white transition-all active:scale-[0.98] disabled:opacity-50"
              >
                {loading ? (
                  <span className="flex items-center justify-center">
                    <svg className="animate-spin h-5 w-5 mr-3 border-2 border-t-transparent border-black rounded-full" viewBox="0 0 24 24"></svg>
                    Synthesizing...
                  </span>
                ) : "Generate Gift Ideas"}
              </button>

              {result && (
                <div className="mt-10 animate-in fade-in slide-in-from-bottom-4 duration-700">
                  <div className="flex items-center space-x-2 mb-4">
                    <div className="h-[1px] flex-1 bg-gray-800"></div>
                    <span className="text-xs text-gray-500 font-mono">AI RESPONSE</span>
                    <div className="h-[1px] flex-1 bg-gray-800"></div>
                  </div>
 <div className="mt-6 text-gray-800">
  <ReactMarkdown>{result}</ReactMarkdown>
</div>
                </div>
              )}
            </div>
          </div>
        </section>

        {/* Features Section */}
        <section id="features" className="max-w-6xl mx-auto px-6 py-24 grid md:grid-cols-3 gap-8">
          <div className="p-8 border border-gray-900 rounded-2xl bg-gradient-to-b from-[#111] to-black">
            <div className="text-purple-500 mb-4">✦</div>
            <h4 className="text-white font-bold mb-2">Neural Curation</h4>
            <p className="text-sm text-gray-500">Sophisticated algorithms that understand the subtle nuances of human relationships.</p>
          </div>
          <div className="p-8 border border-gray-900 rounded-2xl bg-gradient-to-b from-[#111] to-black">
            <div className="text-purple-500 mb-4">✦</div>
            <h4 className="text-white font-bold mb-2">Instant Inspiration</h4>
            <p className="text-sm text-gray-500">Generate hundreds of unique concepts in seconds, ranging from physical items to experiences.</p>
          </div>
          <div className="p-8 border border-gray-900 rounded-2xl bg-gradient-to-b from-[#111] to-black">
            <div className="text-purple-500 mb-4">✦</div>
            <h4 className="text-white font-bold mb-2">Global Trends</h4>
            <p className="text-sm text-gray-500">Real-time data integration ensures your gift ideas are modern and sought-after.</p>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
}

export default App;
