import React, { useState } from "react";
import { generateResponse } from "./api";

function App() {
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!prompt) return alert("Enter a prompt");

    setLoading(true);
    setResult("");

    try {
      const res = await generateResponse({ prompt });
      setResult(res.data.result || res.data.error);
    } catch (err) {
      setResult("Server error.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-purple-600 to-pink-500">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-xl">
        <h1 className="text-3xl font-bold text-center text-purple-600 mb-6">
          üéÅ GiftGenix
        </h1>

        <textarea
          rows="4"
          placeholder="Type your gift idea prompt here..."
          className="w-full border p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
        />

        <button
          onClick={handleSubmit}
          className="w-full bg-purple-600 text-white p-3 rounded-lg mt-4"
        >
          {loading ? "Generating..." : "Generate"}
        </button>

        <div className="mt-6 whitespace-pre-wrap text-gray-700">
          {result}
        </div>
      </div>
    </div>
  );
}

export default App;
