import React, { useState } from "react";
import { generateResponse } from "./api";

// â”€â”€ MAIN LOGIC UNTOUCHED â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

function App() {
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!prompt) return alert("Enter a prompt");

    setLoading(true);
    setResult("");

    try {
      const res = await generateResponse({ prompt });
      setResult(res.data.result || res.data.error);
    } catch (err) {
      setResult("Server error.");
    }

    setLoading(false);
  };

  // â”€â”€ UI ONLY â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;700;900&family=DM+Sans:wght@300;400;500&display=swap');

        *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

        :root {
          --bg:        #080808;
          --surface:   #111111;
          --surface2:  #181818;
          --border:    #242424;
          --gold:      #c9a84c;
          --gold-dim:  #7a612b;
          --text:      #e8e8e8;
          --muted:     #666;
          --white:     #ffffff;
        }

        body {
          background: var(--bg);
          color: var(--text);
          font-family: 'DM Sans', sans-serif;
          min-height: 100vh;
          -webkit-font-smoothing: antialiased;
        }

        /* â”€â”€ HEADER â”€â”€ */
        .header {
          position: sticky;
          top: 0;
          z-index: 100;
          background: rgba(8,8,8,0.85);
          backdrop-filter: blur(16px);
          border-bottom: 1px solid var(--border);
        }
        .header-inner {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          height: 64px;
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .logo {
          font-family: 'Playfair Display', serif;
          font-size: 1.5rem;
          font-weight: 900;
          letter-spacing: -0.02em;
          color: var(--white);
        }
        .logo span { color: var(--gold); }
        .nav { display: flex; gap: 2rem; }
        .nav a {
          font-size: 0.85rem;
          font-weight: 500;
          color: var(--muted);
          text-decoration: none;
          letter-spacing: 0.08em;
          text-transform: uppercase;
          transition: color 0.2s;
        }
        .nav a:hover { color: var(--gold); }
        .badge {
          font-size: 0.7rem;
          background: var(--gold);
          color: #000;
          font-weight: 700;
          padding: 3px 10px;
          border-radius: 100px;
          letter-spacing: 0.06em;
          text-transform: uppercase;
        }

        /* â”€â”€ HERO â”€â”€ */
        .hero {
          max-width: 1200px;
          margin: 0 auto;
          padding: 7rem 2rem 4rem;
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 4rem;
          align-items: center;
        }
        .hero-eyebrow {
          font-size: 0.75rem;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          color: var(--gold);
          margin-bottom: 1.2rem;
          display: flex;
          align-items: center;
          gap: 0.6rem;
        }
        .hero-eyebrow::before {
          content: '';
          width: 30px;
          height: 1px;
          background: var(--gold);
          display: inline-block;
        }
        .hero-title {
          font-family: 'Playfair Display', serif;
          font-size: clamp(2.8rem, 5vw, 4.5rem);
          font-weight: 900;
          line-height: 1.05;
          letter-spacing: -0.03em;
          color: var(--white);
          margin-bottom: 1.4rem;
        }
        .hero-title em {
          font-style: italic;
          color: var(--gold);
        }
        .hero-sub {
          font-size: 1rem;
          line-height: 1.7;
          color: var(--muted);
          max-width: 420px;
          font-weight: 300;
        }

        /* â”€â”€ GENERATOR CARD â”€â”€ */
        .card {
          background: var(--surface);
          border: 1px solid var(--border);
          border-radius: 20px;
          padding: 2.5rem;
          position: relative;
          overflow: hidden;
        }
        .card::before {
          content: '';
          position: absolute;
          top: -60px;
          right: -60px;
          width: 200px;
          height: 200px;
          background: radial-gradient(circle, rgba(201,168,76,0.12) 0%, transparent 70%);
          pointer-events: none;
        }
        .card-label {
          font-size: 0.7rem;
          letter-spacing: 0.15em;
          text-transform: uppercase;
