import React, { useState } from "react";
import { getRecommendations } from "./api";
import Admin from "./Admin";

function App() {
  const [adminMode, setAdminMode] = useState(false);
  const [result, setResult] = useState("");
  const [form, setForm] = useState({
    occasion: "",
    budget: "",
    relationship: "",
    interests: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async () => {
    const res = await getRecommendations(form);
    setResult(res.data.result || res.data.error);
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-xl">
        <h1 className="text-3xl font-bold text-center text-purple-600 mb-6">
          üéÅ GiftGenix
        </h1>

        {!adminMode ? (
          <>
            <input name="occasion" placeholder="Occasion" className="input" onChange={handleChange} />
            <input name="budget" placeholder="Budget" className="input" onChange={handleChange} />
            <input name="relationship" placeholder="Relationship" className="input" onChange={handleChange} />
            <input name="interests" placeholder="Interests" className="input" onChange={handleChange} />

            <button onClick={submit}
              className="w-full bg-purple-600 text-white p-3 rounded-lg mt-4">
              Get Recommendations
            </button>

            <div className="mt-6 whitespace-pre-wrap text-gray-700">
              {result}
            </div>

            <p onClick={() => setAdminMode(true)}
              className="text-sm text-center mt-6 text-gray-400 cursor-pointer">
              Admin Access
            </p>
          </>
        ) : (
          <Admin goBack={() => setAdminMode(false)} />
        )}
      </div>
    </div>
  );
}

export default App;
